import logo from './logo.svg';
import './App.css';
import React, {useState} from 'react';
import bear from './images/bear.png'
import oski from "./images/oski.webp"
import Modal from 'react-modal';
import { auth,   logInWithEmailAndPassword,  registerWithEmailAndPassword,
  signInWithGoogle } from "./firebase";
import { useAuthState } from "react-firebase-hooks/auth";


const App = () =>{
  const [modalIsOpen, setModalIsOpen] = useState(false);
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [user, loading, error] = useAuthState(auth);
    return (
      <div className="container">
        <div className="Bar">
          <img alt="logo" className="photo" src={bear}/>
          <h1 style = {{marginRight: "43%", paddingTop: "10px"}}>StudyBear</h1>
          <h3 className="barText">About us</h3>
          <h3 className="barText">How it works</h3>
          <h3 className="barText">Contact us</h3>
          <button className="topButton" onClick={() => setModalIsOpen(true)}> Join StudyBear!</button>
          <Modal isOpen={modalIsOpen} onRequestClose={() => setModalIsOpen(false)} style={{
            content: {
              marginLeft: "28%",
              marginTop: "100px",
              width: "500px",
              height: "300px"
            }
          }}>
            <div className="login__container">
              <input
              type="text"
              className="login__textBox"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="E-mail Address"
              />
              <input
              type="password"
              className="login__textBox"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="Password"
              />
              <button
                className="login__btn"
                onClick={() => logInWithEmailAndPassword(email, password)}>
                Login
              </button>
            </div>
            <div>
              Don't have an account? <Link to="/register">Register</Link> now.
            </div>
          </Modal>
        </div>
        <div style={{display: "flex", flexDirection: "row"}}>
          <div className="leftTextBox">
            <p className="paragraph"> Tired of working 
            <span style={{color: "#FDB515"}}> alone</span>? Want to meet fellow bears <span style={{color: "#FDB515"}}>studying</span>?</p>
            <p className= "smallerParagraph"> Join StudyBear! A community of Berkeley students looking for fellow bears to study with.</p>
            <button className="topButton" style={{fontSize: "25px", width: "200px"}}>Join now!</button>
          </div>
          <div>
            <img alt="oski" className = "oski" src={oski}/>
          </div>
        </div>
        </div>
    );
}

export default App;
