import logo from './logo.svg';
import './App.css';
import React, {useState} from 'react';
import bear from './images/bear.png'
import oski from "./images/oski.webp"
import Modal from 'react-modal';
import { Link } from 'react-router-dom';
import LogIn from "./pages/logIn";
import Settings from "./pages/Settings";

const App = () =>{
  const [modalIsOpen, setModalIsOpen] = useState(false);
    return (
      <div className="container">
        <div className="Bar">
          <img alt="logo" className="photo" src={bear}/>
          <h1 style = {{marginRight: "43%", paddingTop: "10px"}}>StudyBear</h1>
          <h3 className="barText">About us</h3>
          <h3 className="barText">How it works</h3>
          <h3 className="barText">Contact us</h3>
          <button className="topButton" onClick={() => setModalIsOpen(true)}> Join StudyBear!</button>
          <Modal isOpen={modalIsOpen} onRequestClose={() => setModalIsOpen(false)} style={{
            content: {
              width: "300px"
            }
          }}>
            <h2>Modal Header</h2>
            <p> Modal Boday</p>
            <button onClick={() => setModalIsOpen(false)}>Close</button>
          </Modal>
        </div>
        <div style={{display: "flex", flexDirection: "row"}}>
          <div className="leftTextBox">
            <p className="paragraph"> Tired of working 
            <span style={{color: "#FDB515"}}> alone</span>? Want to meet fellow bears <span style={{color: "#FDB515"}}>studying</span>?</p>
            <p className= "smallerParagraph"> Join StudyBear! A community of Berkeley students looking for fellow bears to study with.</p>
            <button className="topButton" style={{fontSize: "25px", width: "200px"}}>Join now!</button>
          </div>
          <div>
            <img alt="oski" className = "oski" src={oski}/>
          </div>
        </div>
        </div>
    );
}

export default App;
